import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchProfile, updateProfile, fetchProfileStart } from '../features/profileSlice';

const Profile = () => {
    const dispatch = useDispatch();
    const profile = useSelector((state) => state.profile);

    useEffect(() => {
        dispatch(fetchProfile());
    }, [dispatch]);

    useEffect(() => {
        dispatch(fetchProfileStart());
    }, [dispatch]);

    const handleUpdate = (e) => {
        e.preventDefault();
        const formData = new FormData(e.target);
        const updatedProfile = {
            name: formData.get('name'),
            email: formData.get('email'),
        };
        dispatch(updateProfile(updatedProfile));
    };


    return (
        <div>
            <h1>User Profile</h1>
            <form onSubmit={handleUpdate}>
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" defaultValue={profile.name} required />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" defaultValue={profile.email} required />
                </div>
                <button type="submit">Update Profile</button>
            </form>
        </div>
    );
};

export default Profile;
